mapper.select:
SELECT

,

(SELECT GROUP_CONCAT(CONCAT(attr_name, ':', attr_value) SEPARATOR ';')
FROM db_goods.t_specification_info WHERE FIND_IN_SET(mark_id, specification_ids)) AS specifications
FROM t_seckill_info

    
        mark_id = #{markId,jdbcType=VARCHAR}
    
    
        AND product_id = #{productId,jdbcType=VARCHAR}
    
    
        AND product_type = #{productType,jdbcType=INTEGER}
    
    
        AND product_name = #{productName,jdbcType=VARCHAR}
    
    
        AND theme LIKE CONCAR(CONCAT('%', #{theme,jdbcType=VARCHAR}), '%')
    
    
        AND price = #{price,jdbcType=DECIMAL}
    
    
        AND TO_DAYS(start_time) = TO_DAYS(#{startTime,jdbcType=TIMESTAMP})
    
    
        AND TO_DAYS(stop_time) = TO_DAYS()#{stopTime,jdbcType=TIMESTAMP}
    
    
        AND free = #{free,jdbcType=BIT}
    
    
        AND server_status = #{serverStatus,jdbcType=BIT}
