mapper.select:
select

from t_teambuy_info t
where mark_id = #{markId,jdbcType=VARCHAR}

SELECT * from (select

,
(SELECT `name` FROM db_base.t_attribute_info WHERE
`code`=t.limited) AS limitDesc,

,

(SELECT GROUP_CONCAT(CONCAT(attr_name, ':', attr_value) SEPARATOR ';')
FROM db_goods.t_specification_info WHERE FIND_IN_SET(mark_id, i.specification_ids)) AS specifications
FROM t_teambuy_info t LEFT JOIN t_link_item i ON t.mark_id=i.super_id) t

    
        t.mark_id = #{markId,jdbcType=VARCHAR}
    
    
        AND t.product_id = #{productId,jdbcType=VARCHAR}
    
    
        AND t.product_type = #{productType,jdbcType=INTEGER}
    
    
        AND t.product_name = #{productName,jdbcType=VARCHAR}
    
    
        AND t.theme LIKE CONCAT(CONCAT('%', #{theme,jdbcType=VARCHAR}), '%')
    
    
        AND t.type = #{type,jdbcType=INTEGER}
    
    
        AND t.TO_DAYS(start_time) = TO_DAYS(#{startTime,jdbcType=TIMESTAMP})
    
    
        AND t.TO_DAYS(stop_time) = TO_DAYS(#{stopTime,jdbcType=TIMESTAMP})
    
    
        AND t.req_count = #{reqCount,jdbcType=INTEGER}
    
    
        AND t.vaild_time = #{vaildTime,jdbcType=INTEGER}
    
    
        AND t.limited = #{limited,jdbcType=VARCHAR}
    
    
        AND t.free = #{free,jdbcType=BIT}
    
    
        AND t.server_status = #{serverStatus,jdbcType=BIT}
