mapper.select:
select

,
(SELECT u.nick_name FROM t_user_info u WHERE u.mark_id = user_id) AS userName,
(SELECT `name` FROM db_base.t_attribute_info WHERE `code`=cook_style) AS cookStyleDesc
from t_cook_certified

    
        mark_id = #{markId,jdbcType=VARCHAR},
    
    
        AND user_id = #{userId,jdbcType=VARCHAR}
    
    
        AND short_name LIKE CONCAT(CONCAT('%', #{shortName,jdbcType=VARCHAR}), '%')
    
    
        AND cook_style = #{cookStyle,jdbcType=VARCHAR}
    
    
        AND phone = #{phone,jdbcType=VARCHAR}
    
    
        AND province = #{province,jdbcType=VARCHAR}
    
    
        AND city = #{city,jdbcType=VARCHAR}
    
    
        AND address = #{address,jdbcType=VARCHAR}
    
    
        AND certified = #{certified,jdbcType=BIT}
    
    
        AND image_path = #{imagePath,jdbcType=VARCHAR}
