mapper.select:
select

from t_meal_info m
where m.mark_id = #{markId,jdbcType=VARCHAR}

SELECT

FROM t_meal_info m

    
        m.mark_id = #{markId,jdbcType=VARCHAR}
    
    
        AND m.theme LIKE CONCAT('%',
        #{theme,jdbcType=VARCHAR},'%')
    
    
        AND m.base_price = #{basePrice,jdbcType=DECIMAL}
    
    
        AND m.sale_price = #{salePrice,jdbcType=DECIMAL}
    
    
        AND m.stock_size = #{stockSize,jdbcType=INTEGER}
    
    
        AND m.server_status =
        #{serverStatus,jdbcType=BIT}
    
    
        AND m.description =
        #{description,jdbcType=VARCHAR}
    
    
        AND m.sort = #{sort,jdbcType=INTEGER}
    
    
        AND m.meal_no = #{mealNo,jdbcType=VARCHAR}

SELECT
FROM t_meal_info m LEFT JOIN t_column_goods i ON i.goods_id = m.mark_id
NOT EXISTS (SELECT c.mark_id FROM t_column_info c WHERE c.mark_id = i.column_id) AND m.server_status = 1
AND m.mark_id = #{markId,jdbcType=VARCHAR}
AND m.theme LIKE CONCAT('%', #{theme,jdbcType=VARCHAR},'%')
AND m.meal_no = #{mealNo,jdbcType=VARCHAR}

SELECT
FROM t_meal_info m LEFT JOIN t_label_goods i ON i.goods_id = m.mark_id
NOT EXISTS (SELECT c.mark_id FROM t_label_info c WHERE c.mark_id = i.label_id) AND m.server_status = 1
AND m.mark_id = #{markId,jdbcType=VARCHAR}
AND m.theme LIKE CONCAT('%', #{theme,jdbcType=VARCHAR},'%')
AND m.meal_no = #{mealNo,jdbcType=VARCHAR}

SELECT
FROM t_meal_info m
m.server_status = 1 AND NOT EXISTS (SELECT i.mark_id FROM t_special_item i WHERE i.goods_id = m.mark_id)
AND m.mark_id = #{markId,jdbcType=VARCHAR}
AND m.theme LIKE CONCAT('%', #{theme,jdbcType=VARCHAR},'%')
AND m.meal_no = #{mealNo,jdbcType=VARCHAR}

SELECT
FROM t_meal_info m
m.server_status = 1 AND NOT EXISTS (SELECT i.mark_id FROM t_icon_item i WHERE i.goods_id = m.mark_id)
AND m.mark_id = #{markId,jdbcType=VARCHAR}
AND m.theme LIKE CONCAT('%', #{theme,jdbcType=VARCHAR},'%')
AND m.meal_no = #{mealNo,jdbcType=VARCHAR}
