mapper.select:
SELECT
 j.mark_id, j.order_id, j.meal_id,j.user_id, j.add_time, 
 j.server_status,j.description,j.commentator, j.star, j.sort,
 (SELECT theme FROM t_meal_info WHERE mark_id = j.meal_id) AS mealName,
 d.contact,d.phone ,d.delivery_area,d.delivery_address
 FROM t_meal_judge j LEFT JOIN db_order.t_order_delivery d
 ON d.order_id = j.order_id
 
     
         j.mark_id = #{markId,jdbcType=VARCHAR}
     
     
         AND j.meal_id = #{mealId,jdbcType=VARCHAR}
     
     
         AND j.order_id = #{orderId,jdbcType=VARCHAR}
     
     
         AND j.user_id= #{userId,jdbcType=VARCHAR}
     
     
         AND server_status = #{serverStatus,jdbcType=BIT}
     
     
         AND j.commentator =
         #{commentator,jdbcType=VARCHAR}
     
     
         AND j.add_time = #{addTime,jdbcType=TIMESTAMP}
     
     
         AND j.star = #{star,jdbcType=INTEGER}
     
     
         AND j.sort = #{sort,jdbcType=INTEGER}

SELECT j.mark_id AS judgeId, IF(ISNULL(j.description) || LENGTH(TRIM(j.description)) < 1, '此用户未填写评价内容', j.description) AS description,j.add_time AS addTime,j.star, u.nick_name AS nickName, u.header_img AS headerImg FROM t_meal_judge j LEFT JOIN db_user.t_user_info u ON j.user_id=u.mark_id WHERE j.meal_id=#{mealId,jdbcType=VARCHAR}
AND (j.user_id=#{userId} OR j.server_status=1)
AND j.server_status=1
ORDER BY j.star DESC
LIMIT #{number,jdbcType=INTEGER}
