mapper.select:
select

from t_commodity_info c
where mark_id = #{markId,jdbcType=VARCHAR}

SELECT , (SELECT img_id FROM t_commodity_img WHERE commodity_id=c.mark_id ORDER BY sort LIMIT 1) AS imgId, (SELECT GROUP_CONCAT(`name`) from t_category_commodity cc LEFT JOIN t_category_info ON cc.category_id=mark_id WHERE cc.commodity_id=c.mark_id) AS cates FROM t_commodity_info c WHERE STATUS <> -1
and code like concat('%', #{code,jdbcType=VARCHAR}, '%')
and name like concat('%', #{name,jdbcType=VARCHAR}, '%')
and store_id = #{storeId,jdbcType=VARCHAR}
and type = #{type,jdbcType=INTEGER}
and status = #{status,jdbcType=INTEGER}

select , (SELECT img_id FROM t_commodity_img ORDER BY sort LIMIT 1) AS imgId, (SELECT GROUP_CONCAT(`name`) from t_category_commodity cc LEFT JOIN t_category_info ON cc.category_id=mark_id WHERE cc.commodity_id=c.mark_id) AS cates from t_commodity_info c LEFT JOIN t_category_commodity cc on c.mark_id=cc.commodity_id where status <> -1
and code like concat('%', #{code,jdbcType=VARCHAR}, '%')
and name like concat('%', #{name,jdbcType=VARCHAR}, '%')
and store_id = #{storeId,jdbcType=VARCHAR}
and type = #{type,jdbcType=INTEGER}
and status = #{status,jdbcType=INTEGER}
and category_id is null
and category_id=#{cateId,jdbcType=VARCHAR}

select

from t_commodity_info c
where mark_id = #{commodityId,jdbcType=VARCHAR}
